import React, { Component } from "react";
import { Row, Col } from "reactstrap";

class ExperienceCard extends Component {
  state = {
    photoDisplay: false
  };

  photoDisplayer = () => {
    if (this.state.photoDisplay) {
      <div>
        <img
          src="https://felswebsite.s3.amazonaws.com/ThinkspacesHome.png"
          width="100%"
          height="200"
        />
      </div>;
    }
  };

  render() {
    const { name, date, position, tools, summary } = this.props;
    return (
      <div style={{ justifyContent: "space-between" }}>
        <Row>
          <Col xs="2">
            <div style={{ paddingLeft: 70 }}>
              <Row>
                <h3> {name} </h3>
              </Row>
              <Row>{date}</Row>
              <Row>{position}</Row>
              <Row>{tools}</Row>
            </div>
          </Col>
          <Col>{summary}</Col>
        </Row>
        <div
          onClick={this.setState({ photoDisplay: !this.state.photoDisplay })}
        >
          Expand
        </div>
      </div>
    );
  }
}
export default ExperienceCard;
