import React, { Component } from "react";
import { Row, Col, Button } from "reactstrap";
import "./experience-card.css";

class ExperienceCard2 extends Component {
  state = {
    photoDisplay: false
  };

  // toggle = () => {
  //   this.setState({ photoDisplay: !this.state.photoDisplay });
  // };
  //
  // photoDisplayer() {
  //   if (this.state.photoDisplay) {
  //     return (
  //       <div>
  //         <img
  //           src="https://felswebsite.s3.amazonaws.com/ThinkspacesHome.png"
  //           width="100%"
  //           height="200"
  //         />
  //       </div>
  //     );
  //   }
  // }

  render() {
    const {
      name,
      date,
      position,
      tools,
      graph1,
      graph2,
      graph3,
      graphics,
      mainPhoto
    } = this.props;
    console.log(this.state.photoDisplay);
    return (
      <div
        style={{
          justifyContent: "space-between",
          backgroundColor: "#1A1A1A",
          color: "white",
          paddingTop: 20,
          paddingBottom: 20
        }}
      >
        <Row>
          <Col xs="8">
            <div>
              <img src={mainPhoto} width="100%" height="500" />
            </div>
          </Col>
          <Col xs="3">
            <div className="vertical-center">
              <Row>
                <h3> {name} </h3>
              </Row>
              <Row>{date}</Row>
              <Row>{position}</Row>
              <br />
              <Row>{tools}</Row>
              <br />
              <Row>
                <div
                  className="expand-button"
                  onClick={() => {
                    this.setState({ photoDisplay: !this.state.photoDisplay });
                  }}
                >
                  {" "}
                  Read More{" "}
                </div>
              </Row>
            </div>
          </Col>
        </Row>
        {this.state.photoDisplay ? (
          <div style={{ marginRight: "10%", marginLeft: "10%" }}>
            {graph1}
            <br />
            <br />
            {graph2}
            <div />
            <div>{graphics}</div>
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
export default ExperienceCard2;
