import React, { Component } from "react";
import { Row, Col, Button } from "reactstrap";

class ExperienceCard extends Component {
  state = {
    photoDisplay: false
  };

  // toggle = () => {
  //   this.setState({ photoDisplay: !this.state.photoDisplay });
  // };
  //
  // photoDisplayer() {
  //   if (this.state.photoDisplay) {
  //     return (
  //       <div>
  //         <img
  //           src="https://felswebsite.s3.amazonaws.com/ThinkspacesHome.png"
  //           width="100%"
  //           height="200"
  //         />
  //       </div>
  //     );
  //   }
  // }

  render() {
    const { name, date, position, tools, summary } = this.props;
    console.log(this.state.photoDisplay);
    return (
      <div style={{ justifyContent: "space-between" }}>
        <Row>
          <Col xs="2">
            <div style={{ paddingLeft: 70 }}>
              <Row>
                <h3> {name} </h3>
              </Row>
              <Row>{date}</Row>
              <Row>{position}</Row>
              <Row>{tools}</Row>
            </div>
          </Col>
          <Col>{summary}</Col>
        </Row>
        <div
          onClick={() => {
            this.setState({ photoDisplay: !this.state.photoDisplay });
          }}
        >
          {" "}
          expand{" "}
        </div>
        {this.state.photoDisplay ? (
          <div className="image-container">
            <img
              src="https://felswebsite.s3.amazonaws.com/ThinkspacesHome.png"
              width="100%"
              height="100%"
            />
          </div>
        ) : (
          <div />
        )}
      </div>
    );
  }
}
export default ExperienceCard;
